/**
 * \fn int getNumQuadPoints(int dop, int dim)
 * \brief Get the number of quadrature points based on the dop degree of integrand
 * \param dop degree of integrand which is a polynomial
 * \param dim the dimension of space
 * \return the number of quadrature points
 */
int getNumQuadPoints(int dop, int dim)
{
	int num_qp=1; // the number of numerical intergation points

	if(dop<0)
		dop=0;
	
	if(dim==1)
	{		
		num_qp=(dop+2)/2;
		if(num_qp>5)
			num_qp=5;
	}

	if(dim==2)
	{		
		num_qp=pow((dop+2)/2,2);
		if(num_qp>49)
			num_qp=49;
	}

	return num_qp;
}

/**
 * \fn int getNumQuadPoints_ShunnWilliams(int dop, int dim)
 * \brief Get the number of quadrature points of Shunn-Williams rules
 * \param dop degree of integrand which is a polynomial
 * \param dim the dimension of space
 * \return the number of quadrature points
 */
int getNumQuadPoints_ShunnWilliams(int dop, int dim)
{
	int num_qp=1; // the number of numerical intergation points

	if(dop<0)
		dop=0;
	
	if(dim==2)
	{		
		switch (dop) {
		case 0: num_qp=1; break;
		case 1: num_qp=1; break;
		case 2: num_qp=3; break;
		case 3: num_qp=6; break;
		case 4: num_qp=6; break;
		case 5: num_qp=10; break;
		case 6: num_qp=15; break;
		case 7: num_qp=15; break;
		case 8: num_qp=21; break;
		case 9: num_qp=28; break;
		case 10: num_qp=28; break;
		case 11: num_qp=36; break;
		case 12: num_qp=36; break;
		case 13: num_qp=45; break;
		case 14: num_qp=45; break;
		case 15: num_qp=55; break;
		case 16: num_qp=66; break;
		case 17: num_qp=66; break;
		default: num_qp=66;
		}
	}

	if(dim==3)
	{		
		switch (dop) {
		case 0: num_qp=1; break;
		case 1: num_qp=1; break;
		case 2: num_qp=4; break;
		case 3: num_qp=10; break;
		case 4: num_qp=20; break;
		case 5: num_qp=20; break;
		case 6: num_qp=35; break;
		case 7: num_qp=56; break;
		case 8: num_qp=56; break;
		case 9: num_qp=84; break;
		default: num_qp=84;
		}
	}

	return num_qp;
}

