/**
 * \fn void init_Gauss1D(int num_qp, int ncoor, double (*gauss)[2])
 * \brief Initialize Gauss quadrature points and weights, the algebraic precision is 2*num_qp-1
 * \param num_qp the number of quadrature points
 * \param ncoor the dimension of space
 * \param (*gauss)[2] gauss[*][0] -- quad point x in ref coor
 *					  gauss[*][1] -- quad weight
 */
void init_Gauss1D(int num_qp, int ncoor, double (*gauss)[2])
{
	if(num_qp<=0)
	{
		printf(" Number of quadrature points must be positive.\n");
		return;
	}
	
	if(ncoor!=1)
	{
		printf(" Only 1-d quadrature rule supported currently.\n");
		return;
	}
	
	switch (num_qp) {
		case 1:  // 1-point Gauss rule
			gauss[0][0] = 0.5;
			gauss[0][1] = 1.0;
			break;
		case 2:  // 2-point Gauss rule
			gauss[0][0] = (1+sqrt(1.0/3.0))/2.0;
			gauss[0][1] = 0.5;
			
			gauss[1][0] = (1-sqrt(1.0/3.0))/2.0;
			gauss[1][1] = 0.5;
			break;
		case 3:  // 3-point Gauss rule
			gauss[0][0] = 0.5;
			gauss[0][1] = 4.0/9.0;
			
			gauss[1][0] = (1+sqrt(3.0/5.0))/2.0;
			gauss[1][1] = 5.0/18.0;
			
			gauss[2][0] = (1-sqrt(3.0/5.0))/2.0;
			gauss[2][1] = 5.0/18.0;
			break;
		case 4:  // 4-point Gauss rule
			gauss[0][0] = (1+sqrt((3.0-2.0*sqrt(6.0/5.0))/7.0))/2.0;
			gauss[0][1] = (18.0+sqrt(30.0))/72.0;
			
			gauss[1][0] = (1-sqrt((3.0-2.0*sqrt(6.0/5.0))/7.0))/2.0;
			gauss[1][1] = (18.0+sqrt(30.0))/72.0;
			
			gauss[2][0] = (1+sqrt((3.0+2.0*sqrt(6.0/5.0))/7.0))/2.0;
			gauss[2][1] = (18.0-sqrt(30.0))/72.0;
			
			gauss[3][0] = (1-sqrt((3.0+2.0*sqrt(6.0/5.0))/7.0))/2.0;
			gauss[3][1] = (18.0-sqrt(30.0))/72.0;
			break;
		case 5:  // 5-point Gauss rule
			gauss[0][0] = 0.5;
			gauss[0][1] = 128.0/450.0;
			
			gauss[1][0] = (1+sqrt(5.0-2.0*sqrt(10.0/7.0))/3.0)/2.0;
			gauss[1][1] = (322.0+13.0*sqrt(70.0))/1800.0;
			
			gauss[2][0] = (1-sqrt(5.0-2.0*sqrt(10.0/7.0))/3.0)/2.0;
			gauss[2][1] = (322.0+13.0*sqrt(70.0))/1800.0;
			
			gauss[3][0] = (1+sqrt(5.0+2.0*sqrt(10.0/7.0))/3.0)/2.0;
			gauss[3][1] = (322.0-13.0*sqrt(70.0))/1800.0;
			
			gauss[4][0] = (1-sqrt(5.0+2.0*sqrt(10.0/7.0))/3.0)/2.0;
			gauss[4][1] = (322.0-13.0*sqrt(70.0))/1800.0;
			break;
		default:
			printf("%d-point Lagrange rule is not supported.\n",num_qp);
			break;
	}
	
	return;
}

